mapper.select:
SELECT DISTINCT test_paper.paper_name, test_paper.paper_id, user.username, test_paper.create_time, assistcollection.assiststates, assistcollection.collectionstates, assistcollection.user_id FROM test_paper LEFT JOIN assistcollection ON assistcollection.paper_id = test_paper.paper_id LEFT JOIN user ON user.id = assistcollection.user_id
and assistcollection.collectionstates = #{collectionstates}
and test_paper.paper_name like '%${paperName}%'
and #{startTime} < test_paper.create_time
and test_paper.create_time < #{endTime}

mapper.update:
        update assistcollection set
        assiststates =(
	        case
	            when assiststates = 0 then 1
	            when assiststates = 1 then 0
	        else 1 end
        )
        WHERE paper_id=#{paperId}
        and user_id = #{userId}

        update assistcollection set
        collectionstates =(
	        case
	            when collectionstates = 0 then 1
	            when collectionstates = 1 then 0
	        else 1 end
        )
        WHERE paper_id=#{paperId}
        and user_id = #{userId}
